const axios = require("axios").default;

const authenticate = async (user, pass) => {
  try {
    const res = await axios.post(`http://test.elecsis.co/auth/`, {
      user: user,
      pass: pass,
    });

    if (res.data === "Usuario no válido") {
      return false;
    } else {
      window.localStorage.setItem("access_token", res.data);
      window.localStorage.setItem("userName", user);

    }
  } catch (err) {
    console.log(err);
  }
};

export default authenticate;
